const postForm=document.querySelector(".post-block__form"),rangeButtons=postForm?.querySelectorAll(".post-block__icon-button");let activeRangeButton=postForm?.querySelector(".post-block__icon-button--current");function toggleRange(a){const b=document.querySelector(`#${a.dataset.block}`);b?.classList.toggle("post-block__control--current"),a?.classList.toggle("post-block__icon-button--current")}rangeButtons?.forEach(a=>a.addEventListener("click",()=>{toggleRange(activeRangeButton),activeRangeButton=a,toggleRange(activeRangeButton)}));const resetButton=postForm?.querySelector(".post-block__cancel-button");resetButton?.addEventListener("click",()=>{postForm.reset()});const postButtons=document.querySelectorAll(".post-block__icon-button");function deactivatePostButtons(){postButtons?.forEach(a=>{a.setAttribute("role","presentation"),a.setAttribute("aria-hidden","true"),a.setAttribute("tabindex","-1")})}function activatePostButtons(){postButtons?.forEach(a=>{a.removeAttribute("role"),a.removeAttribute("aria-hidden"),a.removeAttribute("tabindex")})}660<=window.innerWidth&&deactivatePostButtons(),window.addEventListener("resize",()=>{requestAnimationFrame(()=>{660<=window.innerWidth&&deactivatePostButtons(),660>window.innerWidth&&activatePostButtons()})});